import React, { Component } from 'react';
import ReactTable from 'react-table-6';
import api from '../api/index.js';

import styled from 'styled-components'

import 'react-table-6/react-table.css'
import { Card, CardDeck } from 'react-bootstrap';

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class Answers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            topics: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({ isLoading: true });

        await api.getAnswerById(this.props.questionId).then(topics => {
            this.setState({
                topics: topics.data.data,
                isLoading: false,
            })
        });
    }

    render() {
        const { topics, isLoading } = this.state
        console.log('TCL: TopicsList -> render -> topics', topics)

        const columns = [
            {
                Header: 'ID',
                accessor: '_id',
                filterable: true,
            },
            {
                Header: 'Topic',
                accessor: 'question',
                filterable: true,
            },
            {
                Header: 'Active',
                accessor: 'active',
                filterable: true,
            }
        ]

        let showTable = true
        if (!topics.length) {
            showTable = false
        }

        return (
            <Wrapper>

                <CardDeck>
                  <Card>
                    <Card.Img variant="top" src="holder.js/100px160" />
                    <Card.Body>
                      <Card.Title>Create a Topic</Card.Title>
                      <Card.Text>
                        <Card.Link href="/topics/create">Click Here</Card.Link>
                      </Card.Text>
                    </Card.Body>
                    <Card.Footer>
                      <small className="text-muted">Last updated 3 mins ago</small>
                    </Card.Footer>
                  </Card>

                  { topics.map((theTopic, key) =>
                    <Card key={key}>
                      <Card.Img variant="top" src="holder.js/100px160" />
                      <Card.Body>
                        <Card.Title>{theTopic.question}</Card.Title>
                        <Card.Text>
                          Submitted by {theTopic.name == null ? 'Anonymous' : theTopic.name}
                        </Card.Text>
                      </Card.Body>
                      <Card.Footer>
                        <Card.Link href="#">See Responses</Card.Link>
                      </Card.Footer>
                    </Card>
                  )}
                </CardDeck>
            </Wrapper>
        )
    }
}

export default Answers
