import React, { Component } from 'react';
import ReactTable from 'react-table-6';
import api from '../api/index.js';

import styled from 'styled-components'

import 'react-table-6/react-table.css'
import { Card, CardDeck, Button, Form } from 'react-bootstrap';
import ListGroup from 'react-bootstrap/ListGroup';

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class Answers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            questionId: '',
            answers: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
        this.setState({
          isLoading: true,
          questionId: window.location.href.split("answers/")[1]
        });

        await api.getAnswerById(window.location.href.split("answers/")[1]).then(topics => {
          console.log(topics);
            this.setState({
                answers: topics.data.data,
                isLoading: false,
            })
        });
    }

    submitAnswer() {
      const payload = {
        questionId: this.state.questionId,
        name: this.state.name,
        answer: this.state.answer
      }

      console.log(payload);

    }

    handleChangeInputName = async event => {
        const name = event.target.value
        console.log(name);

        this.setState({
          name: event.target.value
        });
    }

    handleChangeInputAnswer = async event => {
        const answer = event.target.value
        console.log(answer);

        this.setState({
          answer: event.target.value
        });
    }

    render() {
        return (
            <Wrapper>
              <h2>HI</h2>
              <ListGroup variant="flush">
                <ListGroup.Item>Cras justo odio</ListGroup.Item>
                <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item>Morbi leo risus</ListGroup.Item>
                <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
              </ListGroup>
              <Form>
                <Form.Group controlId="formBasicName">
                  <Form.Label>Name</Form.Label>
                  <Form.Control onChange={this.handleChangeInputName} type="name" placeholder="Enter your name" />
                </Form.Group>

                <Form.Group controlId="formBasicAnswer">
                  <Form.Label>Answer</Form.Label>
                  <Form.Control onChange={this.handleChangeInputAnswer} as="textarea" rows="3" placeholder="Type your answer" />
                </Form.Group>
                <Button onClick={this.submitAnswer} variant="primary" type="submit">
                  Submit your answer
                </Button>
              </Form>
            </Wrapper>
        )
    }
}

export default Answers
