import React, { Component } from 'react';
import ReactTable from 'react-table-6';
import api from '../api/index.js';

import styled from 'styled-components'

import 'react-table-6/react-table.css'
import { Card, CardDeck, Button, Form } from 'react-bootstrap';
import ListGroup from 'react-bootstrap/ListGroup';

const Wrapper = styled.div`
    padding: 0 40px 40px 40px;
`

class Answers extends Component {
    constructor(props) {
        super(props)
        this.state = {
            topics: [],
            columns: [],
            isLoading: false,
        }
    }

    componentDidMount = async () => {
      console.log(window.location.href.split("answers/"));
        this.setState({ isLoading: true });

        await api.getAnswerById(this.props.questionId).then(topics => {
          console.log(topics);
            this.setState({
                topics: topics.data.data,
                isLoading: false,
            })
        });
    }

    render() {
        return (
            <Wrapper>
              <h2>HI</h2>
              <ListGroup variant="flush">
                <ListGroup.Item>Cras justo odio</ListGroup.Item>
                <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                <ListGroup.Item>Morbi leo risus</ListGroup.Item>
                <ListGroup.Item>Porta ac consectetur ac</ListGroup.Item>
              </ListGroup>
              <Form>
                <Form.Group controlId="formBasicName">
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="name" placeholder="Enter your name" />
                </Form.Group>

                <Form.Group controlId="formBasicAnswer">
                  <Form.Label>Answer</Form.Label>
                  <Form.Control as="textarea" rows="3" placeholder="Type your answer" />
                </Form.Group>
                <Button variant="primary" type="submit">
                  Submit your answer
                </Button>
              </Form>
            </Wrapper>
        )
    }
}

export default Answers
