import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',
})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class TopicsInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            question: ''
        }
    }

    handleChangeInputQuestion = async event => {
        const question = event.target.value
        this.setState({ question })
    }

    handleIncludeMovie = async () => {
        const { question } = this.state
        // const arrayTime = time.split('/')
        const payload = { question }

        await api.insertMovie(payload).then(res => {
            window.alert(`Topic inserted successfully`)
            this.setState({
                question: ''
            })
        })
    }

    render() {
        const { question } = this.state
        return (
            <Wrapper>
                <Title>Create Topic</Title>

                <Label>Question: </Label>
                <InputText
                    type="text"
                    value={question}
                    onChange={this.handleChangeInputQuestion}
                />

                <Button onClick={this.handleIncludeMovie}>Add Topic</Button>
                <CancelButton href={'/topics/list'}>Cancel</CancelButton>
            </Wrapper>
        )
    }
}

export default TopicsInsert
