import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:5000/topics',
})

const answersApi = axios.create({
    baseURL: 'http://localhost:5000/answers',
})

export const insertTopic = payload => api.post('/create', payload)
export const getAllTopics = () => api.get('/getTopics')
export const getTopicById = id => api.get('/' + id)
export const getAllAnswers = () => api.get('/getAnswers')
export const getAnswerById = id => answersApi.get('/' + id)
export const addAnswer = payload => answersApi.post('/add', payload)

const apis = {
    insertTopic,
    getAllTopics,
    getTopicById,
    getAnswerById,
    addAnswer
}

export default apis
